         					$('.client-zone-orders-table tbody tr').hide();
         					// display the correct orders table
         					if ( $option.data('moduletypenum') ) {
         						$('.client-zone-orders-table tbody tr[data-moduletypenum="' + $option.data('moduletypenum') + '"]').show();
