         					$html.click(function(){
                                	$('.client-zone-orders-table tbody tr').hide();
         						$('.client-zone-orders-table tbody tr[data-moduletypenum="' + moduleTypeNUM + '"]').show();
         					});
         				}
         				// add the tab html
                               $('.clientZoneTabs').append($html);
                               $('.orders.tab-content').addClass('orders-'+moduleTypeNUM);
